import React, { useEffect } from 'react';
import { Routes, Route, Navigate } from 'react-router-dom';
import { useAuth } from './context/AuthContext';
import Layout from './components/common/Layout';
import Dashboard from './components/dashboard/Dashboard';
import NotFound from './components/common/NotFound';

// Componente simplificado para rutas protegidas
const ProtectedRoute = ({ children }) => {
  const { isAuthenticated } = useAuth();

  // Para facilitar el desarrollo, permitimos el acceso sin autenticación
  // En producción, deberías descomentar esto
  // if (!isAuthenticated) {
  //   return <Navigate to="/login" />;
  // }

  return children;
};

function App() {
  const { checkAuth } = useAuth();

  useEffect(() => {
    // Verificar si hay sesión al cargar la app
    checkAuth();
  }, [checkAuth]);

  return (
    <Routes>
      {/* Eliminamos la ruta de login por ahora */}

      <Route path="/" element={
        <ProtectedRoute>
          <Layout />
        </ProtectedRoute>
      }>
        <Route index element={<Dashboard />} />
        <Route path="dashboard" element={<Dashboard />} />

        {/* Ruta 404 */}
        <Route path="*" element={<NotFound />} />
      </Route>
    </Routes>
  );
}

export default App;
